const payload = $input.item.json;
const payloadData = payload.payload || {};

return {
  json: {
    user_id: payload.user_id,
    from_number: payload.from_number,
    customer_name: payloadData.customer_name || 'Unknown',
    reason: payloadData.reason || 'Meeting request',
    preferred_date: payloadData.preferred_date || 'Not specified',
    preferred_time: payloadData.preferred_time || 'Not specified',
    original_message: payload.original_message,
    created_at: new Date().toISOString()
  }
};

// TODO: Replace with actual calendar integration
