const meetingData = $input.item.json;
const meetingId = 'MTG-' + Date.now();

return {
  json: {
    success: true,
    meeting_id: meetingId,
    customer_name: meetingData.customer_name,
    message: `Meeting ${meetingId} booked for ${meetingData.customer_name} on ${meetingData.preferred_date}`,
    details: meetingData
  }
};
