// Extract order details
const payload = $node["Webhook"].json.payload;
const userId = $node["Webhook"].json.user_id;
const fromNumber = $node["Webhook"].json.from_number;
const originalMessage = $node["Webhook"].json.original_message;

return {
  json: {
    user_id: userId,
    from_number: fromNumber,
    product: payload.product || 'Unknown',
    quantity: payload.quantity || 1,
    notes: payload.notes || '',
    original_message: originalMessage,
    order_date: new Date().toISOString(),
    status: 'pending'
  }
};

// TODO: Add your order creation logic here
// Examples:
// - Insert into database
// - Call your e-commerce API
// - Send to inventory system

const orderData = $node["Extract Order Data"].json;

// Simulate order creation
const orderId = 'ORD-' + Date.now();

return {
  json: {
    success: true,
    order_id: orderId,
    product: orderData.product,
    quantity: orderData.quantity,
    total: orderData.quantity * 100000,
    message: `Order ${orderId} created successfully for ${orderData.product} (Qty: ${orderData.quantity})`
  }
};
