const payload = $input.item.json;
const payloadData = payload.payload || {};

return {
  json: {
    user_id: payload.user_id,
    from_number: payload.from_number,
    customer_name: payloadData.customer_name,
    customer_email: payloadData.customer_email,
    delivery_address: payloadData.delivery_address,
    product: payloadData.items || payloadData.product || 'Unknown',
    quantity: payloadData.quantity || 1,
    price: payloadData.price || 0,
    notes: payloadData.notes || '',
    original_message: payload.original_message,
    order_date: new Date().toISOString(),
    status: 'pending'
  }
};

