const orderData = $input.item.json;
const orderId = 'ORD-' + Date.now();

return {
  json: {
    success: true,
    order_id: orderId,
    customer_name: orderData.customer_name,
    product: orderData.product,
    quantity: orderData.quantity,
    total: orderData.quantity * (orderData.price || 100000),
    message: `Order ${orderId} created for ${orderData.customer_name}. Product: ${orderData.product} (Qty: ${orderData.quantity})`
  }
};
