const handoffData = $input.item.json;
const ticketId = 'TKT-' + Date.now();

return {
  json: {
    success: true,
    ticket_id: ticketId,
    customer_name: handoffData.customer_name,
    message: `Support ticket ${ticketId} created for ${handoffData.customer_name}. Reason: ${handoffData.reason}`
  }
};
