const payload = $input.item.json;
const payloadData = payload.payload || {};

return {
  json: {
    user_id: payload.user_id,
    from_number: payload.from_number,
    customer_name: payloadData.customer_name || 'Unknown',
    items: payloadData.items || 'Unknown',
    quantity: payloadData.quantity || 1,
    customer_phone: payloadData.customer_phone || payload.from_number,
    original_message: payload.original_message,
    order_date: new Date().toISOString()
  }
};

const orderData = $input.item.json;
const orderId = 'PLO-' + Date.now();

return {
  json: {
    success: true,
    order_id: orderId,
    customer_name: orderData.customer_name,
    items: orderData.items,
    quantity: orderData.quantity,
    message: `Order ${orderId} placed for ${orderData.customer_name}. Items: ${orderData.items} (Qty: ${orderData.quantity})`
  }
};
